// Task to install Git pre-commit hook
tasks.register('installGitHooks') {
	description = 'Installs Git pre-commit hook for Spotless formatting checks'
	group = 'build setup'

	doLast {
		def hooksDir = new File(projectDir, '.git/hooks')
		def sourceHook = new File(projectDir, 'scripts/pre-commit')
		def targetHook = new File(hooksDir, 'pre-commit')

		if (!hooksDir.exists()) {
			hooksDir.mkdirs()
		}

		if (sourceHook.exists()) {
			targetHook.text = sourceHook.text
			targetHook.setExecutable(true)
			println "✅ Git pre-commit hook installed successfully"
		} else {
			println "⚠️  Warning: scripts/pre-commit not found, skipping hook installation"
		}
	}
}

// Automatically install hooks when building
tasks.named('build') {
	dependsOn 'installGitHooks'
}

