/**
 * Reactive Kafka consumer that scopes activity events to a developer session's partition/region.
 */
@Service
@ConditionalOnProperty(
    value = "app.ui.activity-stream.enabled",
    havingValue = "true",
    matchIfMissing = true)
public class ActivityStreamKafkaConsumer {

  private static final Logger LOGGER = LoggerFactory.getLogger(ActivityStreamKafkaConsumer.class);
  private static final TypeReference<Map<String, Object>> SUMMARY_TYPE = new TypeReference<>() {};

  private final KafkaProperties kafkaProperties;
  private final ObjectMapper objectMapper;
  private final String topicPrefix;
  private final String defaultTopic;
  private final String partitionTopicPrefix;
  private final Retry retry;
  private final CircuitBreaker circuitBreaker;
  private final AdminClient adminClient;

  public ActivityStreamKafkaConsumer(
      KafkaProperties kafkaProperties,
      ObjectMapper objectMapper,
      @Value("${app.ui.activity-stream.topic-prefix:activity-stream-}") String topicPrefix,
      @Value("${app.ui.activity-stream.default-topic:activity-stream}") String defaultTopic,
      @Value("${app.ui.activity-stream.partition-topic-prefix:se2-}") String partitionTopicPrefix,
      RetryRegistry retryRegistry,
      CircuitBreakerRegistry circuitBreakerRegistry,
      AdminClient adminClient) {
    this.kafkaProperties = kafkaProperties;
    this.objectMapper = objectMapper;
    this.topicPrefix = topicPrefix;
    this.defaultTopic = defaultTopic;
    this.partitionTopicPrefix = partitionTopicPrefix;
    this.retry = retryRegistry.retry("activityStreamKafka");
    this.circuitBreaker = circuitBreakerRegistry.circuitBreaker("activityStreamKafka");
    this.adminClient = adminClient;
  }

  /** Creates a hot {@link Flux} of activity events scoped to the provided session and topic. */
  public Flux<ActivityEventView> receive(ActivityStreamSession session, String topic) {
    ReceiverOptions<String, String> options = receiverOptionsFor(session);

    return KafkaReceiver.create(options.subscription(Collections.singleton(topic)))
        .receive()
        .transformDeferred(CircuitBreakerOperator.of(circuitBreaker))
        .transformDeferred(RetryOperator.of(retry))
        .flatMap(
            record -> {
              try {
                ActivityEventView view = toActivityEventView(record.value(), session);
                if (!session.partition().equals(view.partition())) {
                  record.receiverOffset().acknowledge();
                  return Mono.<ActivityEventView>empty();
                }
                if (session.region() != null && !Objects.equals(session.region(), view.region())) {
                  record.receiverOffset().acknowledge();
                  return Mono.empty();
                }

                record.receiverOffset().acknowledge();
                return Mono.just(view);
              } catch (Exception ex) {
                LOGGER.warn(
                    "Failed to decode Kafka record for session {}: {}",
                    session.sessionId(),
                    ex.getMessage(),
                    ex);
                record.receiverOffset().acknowledge();
                return Mono.empty();
              }
            })
        .doOnSubscribe(
            subscription ->
                LOGGER.info(
                    "Activity stream session {} subscribed to topic {}",
                    session.sessionId(),
                    topic))
        .doOnCancel(() -> LOGGER.info("Activity stream session {} cancelled", session.sessionId()))
        .doOnError(
            error ->
                LOGGER.error(
                    "Activity stream consumer error for session {}: {}",
                    session.sessionId(),
                    error.getMessage(),
                    error));
  }

  private ReceiverOptions<String, String> receiverOptionsFor(ActivityStreamSession session) {
    Map<String, Object> props = kafkaProperties.buildConsumerProperties();
    props.put(ConsumerConfig.GROUP_ID_CONFIG, "activity-stream-ui-" + session.sessionId());
    props.put(ConsumerConfig.KEY_DESERIALIZER_CLASS_CONFIG, StringDeserializer.class);
    props.put(ConsumerConfig.VALUE_DESERIALIZER_CLASS_CONFIG, StringDeserializer.class);
    props.put(ConsumerConfig.AUTO_OFFSET_RESET_CONFIG, "latest");
    props.put(ConsumerConfig.ENABLE_AUTO_COMMIT_CONFIG, false);

    return ReceiverOptions.<String, String>create(props)
        .commitInterval(java.time.Duration.ZERO)
        .commitBatchSize(0);
  }

  /** Determines the Kafka topic backing the session and whether it exists. */
  public Mono<TopicAvailability> resolveTopic(ActivityStreamSession session) {
    return Mono.fromCallable(() -> determineTopic(session))
        .subscribeOn(Schedulers.boundedElastic());
