  private ActivityEventView toActivityEventView(String payload, ActivityStreamSession session)
      throws IOException {
    JsonNode node = objectMapper.readTree(payload);

    String eventId = text(node, "eventId");
    if (eventId == null) {
      eventId = text(node, "messageId");
    }
    if (eventId == null) {
      eventId = UUID.randomUUID().toString();
    }

    String eventType = text(node, "eventType");
    if (eventType == null) {
      eventType = text(node, "type");
    }
    if (eventType == null) {
      eventType = "unknown";
    }

    Instant ingestedAt = parseInstant(text(node, "ingestedAt"));
    if (ingestedAt == null) {
      ingestedAt = parseInstant(text(node, "timestamp"));
    }
    if (ingestedAt == null) {
      ingestedAt = Instant.now();
    }

    String partition = text(node, "partition");
    if (partition == null) {
      partition = session.partition();
    }

    String region = text(node, "region");
    if (region == null) {
      region = session.region();
    }

    Map<String, Object> summary = extractSummary(node);
    JsonNode payloadNode = extractPayload(node);

    return new ActivityEventView(
        eventId, ingestedAt, eventType, partition, region, summary, payloadNode);
