import org.apache.kafka.clients.CommonClientConfigs;
import org.apache.kafka.clients.consumer.ConsumerConfig;
import org.apache.kafka.common.config.SaslConfigs;
import org.apache.kafka.common.security.plain.PlainLoginModule;
import org.apache.kafka.common.serialization.StringDeserializer;
import org.springframework.beans.factory.annotation.Value;
import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;
import org.springframework.kafka.annotation.EnableKafka;
import org.springframework.kafka.config.ConcurrentKafkaListenerContainerFactory;
import org.springframework.kafka.core.ConsumerFactory;
import org.springframework.kafka.core.DefaultKafkaConsumerFactory;
import org.springframework.kafka.support.serializer.JsonDeserializer;

/** Configuration class for setting up Kafka consumers. */
@Slf4j
@EnableKafka
@Configuration
public class KafkaConsumerConfig {

  @Value(value = "${spring.kafka.bootstrap-servers}")
  private String bootstrapAddress;

  @Value(value = "${spring.kafka.properties.user}")
  private String kafkaUser;

  @Value(value = "${spring.kafka.properties.password}")
  private String kafkaPassword;

  @Value("${spring.profiles.active:default}")
  private String activeProfile;

  private final String groupId = "testing";

  /**
   * Configures the consumer factory for Kafka.
   *
   * @return a ConsumerFactory instance for Kafka consumers
   */
  @Bean
  public ConsumerFactory<String, String> consumerFactory() {
    var props = buildConsumerProperties();

    try {
      return new DefaultKafkaConsumerFactory<>(props);
    } catch (Exception e) {
      log.warn("Failed to create Kafka Consumer Factory: {}", e.getMessage(), e);
      throw e;
    }
  }

  /**
   * Builds the properties for Kafka consumers.
   *
   * @return a map containing Kafka consumer properties
   */
  private Map<String, Object> buildConsumerProperties() {
    var props = new HashMap<String, Object>();
    props.put(ConsumerConfig.BOOTSTRAP_SERVERS_CONFIG, bootstrapAddress);
    props.put(ConsumerConfig.GROUP_ID_CONFIG, groupId);
    props.put(ConsumerConfig.KEY_DESERIALIZER_CLASS_CONFIG, StringDeserializer.class);
