  @LogExecution
  public Mono<Void> backup(FileMetadata fileMetadata, boolean remove) {
    if (!backupEnabled) {
      log.debug("Backup is disabled.");
      integrationStateRecorder.markDisabled("backup", "disabled via configuration").subscribe();
      return Mono.empty();
    }
    if (s3client == null) {
