   */
  @LogExecution
  private Mono<Void> ensureBucketExists(String bucketName) {
    return Mono.fromCallable(
            () -> {
              if (!s3client.doesBucketExistV2(bucketName)) {
                log.debug("Bucket '{}' does not exist. Creating it.", bucketName);
                s3client.createBucket(bucketName);
              }
              return true;
            })
        .subscribeOn(Schedulers.boundedElastic())
        .then();
  }

  /**
   * Uploads a file to the specified S3 bucket asynchronously.
   *
   * @param bucketName the name of the bucket.
   * @param fileMetadata metadata of the file to upload.
   * @return a {@link Mono<Void>} indicating completion.
   */
  @LogExecution
  private Mono<Void> uploadFile(String bucketName, FileMetadata fileMetadata) {
    return Mono.fromCallable(
            () -> {
              var metadata = buildObjectMetadata(fileMetadata);
              try (var stream = new FileInputStream(fileMetadata.getPath().toFile())) {
                TransferManager transferManager =
                    TransferManagerBuilder.standard().withS3Client(s3client).build();
                PutObjectRequest putObjectRequest =
                    new PutObjectRequest(bucketName, fileMetadata.getFileName(), stream, metadata);
                var upload = transferManager.upload(putObjectRequest);
                upload.waitForCompletion();
                log.debug("Uploaded file '{}' to S3.", fileMetadata.getFileName());
              }
              return true;
            })
        .subscribeOn(Schedulers.boundedElastic())
        .then();
  }

  /**
   * Removes the local file after backup asynchronously.
   *
   * @param path the path to the local file.
   * @return a {@link Mono<Void>} indicating completion.
   */
  @LogExecution
  private Mono<Void> removeLocalFile(Path path) {
    return Mono.fromRunnable(
            () -> {
              if (!path.toFile().delete()) {
                log.warn("Failed to delete local file at '{}'", path);
              }
            })
        .subscribeOn(Schedulers.boundedElastic())
        .then();
  }

  /**
   * Builds S3 object metadata for the specified file.
   *
   * @param fileMetadata metadata of the file.
   * @return the constructed {@link ObjectMetadata}.
   */
  @LogExecution
  private ObjectMetadata buildObjectMetadata(FileMetadata fileMetadata) {
    var metadata = new ObjectMetadata();
    metadata.setContentLength(fileMetadata.getContentLength());
    metadata.setHeader("x-amz-acl", "private");

    var userMetadata = new HashMap<String, String>();
    userMetadata.put("partition", fileMetadata.getPartition());
    metadata.setUserMetadata(userMetadata);

    return metadata;
  }
}
