    })
@EnabledIfEnvironmentVariable(named = "GEOIP_ENABLED", matches = "(?i)true")
class GeoIPDatabaseIT {

  private static String bucketName;
  private static String fileName;
  private static String accessKey;
  private static String secretKey;
  private static String endpoint;
  private static String region;

  @BeforeAll
  static void loadEnv() {
    bucketName = requireEnv("GEOIP_BUCKET_NAME");
    fileName = requireEnv("GEOIP_FILE_NAME");
    accessKey = requireEnv("S3_ACCESS_KEY");
    secretKey = requireEnv("S3_SECRET_KEY");
    endpoint = requireEnv("S3_ENDPOINT");
    region = requireEnv("S3_REGION");
  }

  @DynamicPropertySource
  static void registerProperties(DynamicPropertyRegistry registry) {
    registry.add("geoip.s3.bucket-name", () -> bucketName);
    registry.add("geoip.s3.file-name", () -> fileName);
    registry.add("app.services.geoip.enabled", () -> "true");
  }

  @Autowired private GeoIPService geoIPService;

  @Test
  void resolvesGeoLocationUsingS3BackedDatabase() {
    GeoLocationDTO result = geoIPService.getGeoData("194.144.179.29").block(DEFAULT_TIMEOUT);

    assertThat(result).isNotNull();
    // GeoIPService.empty() sets only location_of/labeld, so ensure real data came back.
    assertThat(result.getCountry()).isNotBlank();
    assertThat(result.getLatitude()).isNotNull();
    assertThat(result.getLongitude()).isNotNull();

    System.out.printf("Resolved GeoIP: %s%n", result);
  }

  private static final Duration DEFAULT_TIMEOUT = Duration.ofSeconds(15);

  private static String requireEnv(String name) {
    return Objects.requireNonNull(System.getenv(name), name + " environment variable must be set");
  }

  @TestConfiguration
