{"output":"package com.contextsuite.inbox.service;

import com.amazonaws.util.IOUtils;
import com.contextsuite.inbox.annotation.LogExecution;
import com.contextsuite.inbox.dto.FileMetadata;
import java.io.FileInputStream;
import java.nio.file.Path;
import java.util.regex.Pattern;
import lombok.extern.slf4j.Slf4j;
import org.springframework.stereotype.Service;
import reactor.core.publisher.Mono;
import reactor.core.scheduler.Schedulers;

/** Reactive service for extracting and managing metadata from files. */
@Service
@Slf4j
public class MetadataService {

  private static final Pattern DATE_FINDER_PATTERN =
      Pattern.compile(".*(?<year>\\d{4})-(?<month>\\d{2})-(?<day>\\d{2}).*");

  /**
   * Extracts metadata for a given file asynchronously.
   *
   * @param path the file's path.
   * @param partition the partition to which the file belongs.
   * @return a {@link Mono<FileMetadata>} containing the extracted metadata.
   */
  @LogExecution
  public Mono<FileMetadata> getFileMetadata(Path path, String partition) {
    return getContentLength(path)
        .map(
            contentLength ->
                FileMetadata.builder()
                    .path(path)
                    .partition(partition)
                    .fileName(buildFilename(path.getFileName().toString(), partition))
                    .contentLength(contentLength)
                    .build());
  }

  /**
   * Retrieves the content length of the specified file asynchronously.
   *
   * @param path the file's path.
   * @return a {@link Mono<Long>} containing the content length in bytes.
   */
  @LogExecution
  private Mono<Long> getContentLength(Path path) {
    return Mono.fromCallable(
            () -> {
              try (var sampleStream = new FileInputStream(path.toFile())) {
                var byteArray = IOUtils.toByteArray(sampleStream);
                return (long) byteArray.length;
              }
            })
        .subscribeOn(Schedulers.boundedElastic())
        .doOnError(e -> log.error("Error retrieving content length for file: {}", path, e))
        .onErrorReturn(0L);
  }

  /**
   * Builds the filename based on the provided filename and partition. The filename is adjusted to
   * include the date hierarchy if a date pattern is found.
   *
   * @param filename the original filename.
   * @param partition the partition to prepend to the filename.
   * @return the constructed filename.
   */
  @LogExecution
  private String buildFilename(String filename, String partition) {
    var matcher = DATE_FINDER_PATTERN.matcher(filename);
    if (matcher.matches()) {
      filename =
          String.format(
              "%s/%s/%s/%s",
              matcher.group("year"), matcher.group("month"), matcher.group("day"), filename);
    }
    if (partition != null) {
      filename = partition + "/" + filename;
    }
    return filename;
  }
}
