import java.util.Map;
import org.apache.kafka.clients.CommonClientConfigs;
import org.apache.kafka.common.config.SaslConfigs;
import org.apache.kafka.common.security.plain.PlainLoginModule;
import org.springframework.beans.factory.annotation.Value;
import org.springframework.stereotype.Component;

/**
 * Builds authentication settings shared by all Kafka clients so producer and consumer stay aligned.
 */
@Component
public class KafkaClientSecurity {

  private final String activeProfile;
  private final String kafkaUser;
  private final String kafkaPassword;

  public KafkaClientSecurity(
      @Value("${spring.profiles.active:default}") String activeProfile,
      @Value("${spring.kafka.properties.user:}") String kafkaUser,
      @Value("${spring.kafka.properties.password:}") String kafkaPassword) {
    this.activeProfile = activeProfile;
    this.kafkaUser = kafkaUser;
    this.kafkaPassword = kafkaPassword;
  }

  /** Adds SASL configuration if the profile is not dev and credentials are present. */
  public void applySecurity(Map<String, Object> config) {
    if ("dev".equals(activeProfile) || kafkaUser.isEmpty()) {
      return;
    }

    config.put(CommonClientConfigs.SECURITY_PROTOCOL_CONFIG, "SASL_PLAINTEXT");
    config.put(SaslConfigs.SASL_MECHANISM, "PLAIN");
    config.put(
        SaslConfigs.SASL_JAAS_CONFIG,
        String.format(
            "%s required username=\"%s\" password=\"%s\";",
            PlainLoginModule.class.getName(), kafkaUser, kafkaPassword));
  }
}
