import com.maxmind.geoip2.DatabaseReader;
import jakarta.annotation.PreDestroy;
import java.io.File;
import java.io.FileOutputStream;
import java.io.IOException;
import java.io.InputStream;
import java.nio.file.Files;
import java.nio.file.Path;
import java.nio.file.Paths;
import lombok.RequiredArgsConstructor;
import lombok.extern.slf4j.Slf4j;
import org.springframework.beans.factory.annotation.Value;
import org.springframework.boot.ApplicationRunner;
import org.springframework.boot.autoconfigure.condition.ConditionalOnProperty;
import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;
import reactor.core.publisher.Mono;
import reactor.core.scheduler.Schedulers;
import software.amazon.awssdk.core.ResponseInputStream;
import software.amazon.awssdk.services.s3.S3Client;
import software.amazon.awssdk.services.s3.model.GetObjectRequest;
import software.amazon.awssdk.services.s3.model.GetObjectResponse;
import software.amazon.awssdk.services.s3.model.S3Exception;

@Configuration
@ConditionalOnProperty(
    name = "app.services.geoip.enabled",
    havingValue = "true",
    matchIfMissing = false)
@RequiredArgsConstructor
@Slf4j
public class GeoIPConfig {

  @Value("${geoip.s3.bucket-name}")
  private String bucketName;

  @Value("${geoip.s3.file-name}")
  private String geoIpFileName;

  private final S3Client s3client;
  private final GeoIPState geoIPState;

  private static final String LOCAL_CACHE_DIR = "/tmp/geoip";
  private static final long MIN_FREE_SPACE_MB = 100; // Minimum 100MB free space required

  /** Loads the MaxMind GeoIP database from MinIO/S3 into memory eagerly at startup. */
  @Bean
  public Mono<DatabaseReader> geoIPDatabaseReader() {
    Mono<DatabaseReader> dbMono =
        loadGeoIPDatabase().doOnNext(reader -> geoIPState.reader().set(reader)).cache();

    // Eagerly subscribe to trigger loading at startup
    dbMono
        .subscribeOn(Schedulers.boundedElastic())
        .subscribe(
            reader -> log.info("GeoIP database loaded successfully"),
            error -> log.error("Failed to load GeoIP database at startup", error));

    return dbMono;
  }

  /** Fetches the latest GeoIP database from S3/MinIO or local cache and loads it into memory. */
  public Mono<DatabaseReader> loadGeoIPDatabase() {
    return Mono.fromCallable(
            () -> {
              Path localFilePath = Paths.get(LOCAL_CACHE_DIR, geoIpFileName);

              // Try to load from local cache first
              if (Files.exists(localFilePath)) {
                try {
                  log.info("Loading GeoIP database from local cache: {}", localFilePath);
                  DatabaseReader reader =
                      new DatabaseReader.Builder(localFilePath.toFile()).build();
                  DatabaseReader existing = geoIPState.reader().getAndSet(reader);
                  if (existing != null) {
                    existing.close();
                  }
                  return reader;
                } catch (Exception e) {
                  log.warn(
                      "Failed to load GeoIP database from local cache, will try S3: {}",
                      e.getMessage());
                  // Delete corrupted file
                  try {
                    Files.deleteIfExists(localFilePath);
                  } catch (IOException deleteError) {
                    log.debug(
                        "Could not delete corrupted cache file: {}", deleteError.getMessage());
                  }
                }
              }

              // Load from S3 and cache locally if space permits
              if (s3client == null) {
                String error = "S3 client not available and no local cache exists";
                log.error(error);
                throw new RuntimeException(error);
              }

              log.info("Downloading GeoIP database from S3: {}/{}", bucketName, geoIpFileName);

              GetObjectRequest request =
                  GetObjectRequest.builder().bucket(bucketName).key(geoIpFileName).build();

              try (ResponseInputStream<GetObjectResponse> response = s3client.getObject(request);
                  InputStream inputStream = response) {
                // Try to cache locally if we have enough disk space
                if (hasEnoughDiskSpace()) {
                  try {
                    Files.createDirectories(Paths.get(LOCAL_CACHE_DIR));
                    File tempFile = new File(LOCAL_CACHE_DIR, geoIpFileName + ".tmp");

                    // Download to temp file first
                    try (FileOutputStream fos = new FileOutputStream(tempFile)) {
                      byte[] buffer = new byte[8192];
                      int bytesRead;
                      while ((bytesRead = inputStream.read(buffer)) != -1) {
                        fos.write(buffer, 0, bytesRead);
                      }
                    }

                    // Rename to final location
                    Files.move(
                        tempFile.toPath(),
                        localFilePath,
                        java.nio.file.StandardCopyOption.REPLACE_EXISTING);
                    log.info("GeoIP database cached locally at: {}", localFilePath);

                    // Load from the cached file
                    DatabaseReader reader =
                        new DatabaseReader.Builder(localFilePath.toFile()).build();
                    DatabaseReader existing = geoIPState.reader().getAndSet(reader);
                    if (existing != null) {
                      existing.close();
                    }
                    return reader;
                  } catch (Exception e) {
                    log.warn(
                        "Failed to cache GeoIP database locally, loading from stream: {}",
                        e.getMessage());
                    // Fall through to load from stream
                  }
                }

                // Load directly from S3 stream if caching failed or not enough space
                log.info("Loading GeoIP database directly from S3 (no local cache)");
                DatabaseReader reader = new DatabaseReader.Builder(inputStream).build();
                DatabaseReader existing = geoIPState.reader().getAndSet(reader);
                if (existing != null) {
                  existing.close();
                }
                return reader;
              } catch (S3Exception e) {
                String error =
                    String.format(
                        "Failed to download GeoIP database from S3 bucket '%s', file '%s': %s",
                        bucketName, geoIpFileName, e.awsErrorDetails().errorMessage());
                log.error(error, e);
                throw new RuntimeException(error, e);
              } catch (Exception e) {
                String error =
                    String.format(
                        "Failed to read GeoIP database stream from S3 bucket '%s', file '%s': %s",
                        bucketName, geoIpFileName, e.getMessage());
                log.error(error, e);
                throw new RuntimeException(error, e);
              }
            })
        .subscribeOn(Schedulers.boundedElastic());
  }

  private boolean hasEnoughDiskSpace() {
    try {
      File tmpDir = new File("/tmp");
      long freeSpaceMB = tmpDir.getFreeSpace() / (1024 * 1024);
      boolean hasSpace = freeSpaceMB >= MIN_FREE_SPACE_MB;
      if (!hasSpace) {
        log.debug(
            "Insufficient disk space in /tmp: {}MB free (need {}MB)",
            freeSpaceMB,
            MIN_FREE_SPACE_MB);
      }
      return hasSpace;
    } catch (Exception e) {
      log.debug("Could not check disk space: {}", e.getMessage());
      return false;
    }
  }

  /** Ensures GeoIP database is loaded before application is ready. */
  @Bean
  public ApplicationRunner geoIPDatabaseLoader(Mono<DatabaseReader> databaseReaderMono) {
    return args -> {
      log.info("Waiting for GeoIP database to load before marking application as ready...");
      databaseReaderMono.block();
      log.info("GeoIP database is ready");
    };
  }
