{"output":"package com.contextsuite.inbox.config;

import java.time.Duration;
import java.util.concurrent.TimeUnit;
import lombok.RequiredArgsConstructor;
import lombok.extern.slf4j.Slf4j;
import org.apache.kafka.clients.admin.AdminClient;
import org.apache.kafka.clients.admin.DescribeClusterOptions;
import org.apache.kafka.common.KafkaFuture;
import org.springframework.beans.factory.annotation.Value;
import org.springframework.boot.actuate.health.Health;
import org.springframework.boot.actuate.health.HealthIndicator;
import org.springframework.boot.context.event.ApplicationReadyEvent;
import org.springframework.context.event.EventListener;
import org.springframework.stereotype.Component;
import reactor.core.publisher.Mono;
import reactor.core.scheduler.Schedulers;

@Component
@Slf4j
@RequiredArgsConstructor
public class KafkaReadinessHealthIndicator implements HealthIndicator {

  private final AdminClient adminClient;

  @Value("${spring.kafka.bootstrap-servers}")
  private String bootstrapServers;

  private static final Duration TIMEOUT = Duration.ofSeconds(5);
  private volatile Health lastHealth = Health.unknown().build();

  @EventListener(ApplicationReadyEvent.class)
  public void warmup() {
    Mono.fromCallable(this::probe)
        .subscribeOn(Schedulers.boundedElastic())
        .retry(3)
        .subscribe(
            health -> lastHealth = health,
            ex -> log.warn("Kafka readiness warmup failed: {}", ex.getMessage()));
  }

  @Override
  public Health health() {
    Health health = probe();
    lastHealth = health;
    return health;
  }

  private Health probe() {
    try {
      DescribeClusterOptions options =
          new DescribeClusterOptions().timeoutMs((int) TIMEOUT.toMillis());
      KafkaFuture<String> clusterIdFuture = adminClient.describeCluster(options).clusterId();
      String clusterId = clusterIdFuture.get(TIMEOUT.toMillis(), TimeUnit.MILLISECONDS);
      return Health.up().withDetail("clusterId", clusterId).build();
    } catch (Exception ex) {
      log.warn("Kafka readiness check failed: {}", ex.getMessage());
      return Health.down(ex).withDetail("bootstrapServers", bootstrapServers).build();
    }
  }
}
